mapper.select:
SELECT ri_seq, ri_mi_seq, ri_name, ri_min_price, ri_delivery_fee, ri_address, ri_open_time, ri_end_time, ri_description, ri_cate_seq, cate_name, ri_img_seq, img_file FROM restaurant_view WHERE ri_mi_seq = #{memberSeq}
AND ri_name Like CONCAT('%',#{keyword},'%')
LIMIT 20 OFFSET #{offset}

mapper.update:
UPDATE restaurant_info
SET 
    ri_name=#{restName},
    ri_min_price=#{restMinPrice},
    ri_delivery_fee=#{restDeliveryFee},
    ri_address=#{restAddress},
    ri_cate_seq=#{restCateSeq}
    ri_open_time=#{restOpenTime}
    ri_end_time=#{restEndTime}
    ri_description=#{restDescription}
WHERE ri_seq= #{restSeq} AND ri_mi_seq = #{meberSeq}
